import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import { Formik, Form, useField, useFormikContext } from "formik";
import styled from "@emotion/styled";


function TextInput({label,...props}){
	const [field,meta] = useField(props);
	const isPassword=props.name==="password"?true:false;
	return(
			<div>
				<label htmlFor = {props.name}>{label}</label>
				<input className="text-input" {...field} {...props} />
				{meta.touched && meta.error ? (<div className="error">{meta.error}

				</div>):null}

			</div>

		);
}

// Styled components ....

const StyledLabel = styled.label`
  margin-top: 1rem;
`;

const StyledSelect = styled.select`
  color: var(--blue);
`;

const StyledErrorMessage = styled.div`
  font-size: 12px;
  color: var(--red-600);
  width: 400px;
  margin-top: 0.25rem;
  &:before {
    content: "❌ ";
    font-size: 10px;
  }
  @media (prefers-color-scheme: dark) {
    color: var(--red-300);
  }
`;


function SelectInput({label,...props}){
	const [field,meta]=useField(props);

	return (
			<div>
				<StyledLabel htmlFor={props.name}>{label}</StyledLabel>
				<StyledSelect {...field} {...props}/>
				{meta.touched && meta.error ? (<StyledErrorMessage>{meta.error}
				</StyledErrorMessage>):null}
			</div>

		);
}

class LoginForm extends React.Component{
	constructor(props){
		super(props);
		this.state={
			"isLogggedIn":false,

		};

	}
	render(){

		return(
			<Form>
				<TextInput 
					label="Username"
					name="username"
					type="text"
					placeholder="Annen"/>

				<TextInput 
					label="Password"
					name="password"
					type="password"
					placeholder="annen"/>

				<button type="submit">Login</button>
			</Form>
			);
		}

}


function App() {
  return <LoginForm />;
}

ReactDOM.render(<App />, document.getElementById("root"));
