import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';


class EssayForm extends React.Component {

    constructor(props) {
        super(props);
        this.state={
        	value: 'Annen yanımda',
        };
        this.handleChange=this.handleChange.bind(this);
        this.handleSubmit=this.handleSubmit.bind(this);

    }
    handleChange(event) {
    	this.setState({value:event.target.value});
    }
    handleSubmit(event) {
    	alert('submission made : '+this.state.value);
    	event.preventDefault();
    }

    render() {
        return (
        	<form onSubmit={this.handleSubmit}>
        		<label>
        		Essay:

        		<textarea value={this.state.value} onClick={this.handleChange}/>
        		</label>
        		<input type="submit" value="Submit" />
        	</form>
        );
    }
}

class FlavourForm extends React.Component{

	constructor(props){
		super(props);
		this.state={value:'niggas',

		};
		this.handleChange=this.handleChange.bind(this);
		this.handleSubmit=this.handleSubmit.bind(this);
	}
	handleChange(event){
		this.setState({value:event.target.value});
	}
	handleSubmit(event){
		alert('Your favourite flavor is ' + this.state.value);
		event.preventDefault();
	}
	render(){
		return (
			<form onSubmit={this.handleSubmit}>
				<label>
				fLAOUER:
				<select value={this.state.value} onChange={this.handleChange}>
					<option value="annen">annane</option>
					<option value="baban">baban</option>
					<option value="nenen">niggas</option>
					<option value="dedn">dden</option>
				</select>
				<select multiple={true} value={['B', 'C']}>
				</select>
				</label>
				<input type="submit" value="Submit"/>
			</form>

		);
	}
}



let element = <FlavourForm />
ReactDOM.render(
    element,
    document.getElementById('root')
);
