import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import { Formik, Form, useField, useFormikContext } from "formik";
import styled from "@emotion/styled";
import "./styles.css";
import "./styles-custom.css";
import * as Yup from "yup";


function TextInput({label,...props}){
	const [field,meta] = useField(props);
	const isPassword=props.name==="password"?true:false;
	return(
			<div>
				<label htmlFor = {props.name}>{label}</label>
				<input className="text-input" {...field} {...props} />
				{meta.touched && meta.error ? (<StyledErrorMessage>{meta.error}

				</StyledErrorMessage>):null}

			</div>

		);
}


const StyledLabel = styled.label`
  margin-top: 1rem;
`;

const StyledSelect = styled.select`
  color: var(--blue);
`;

const StyledErrorMessage = styled.div`
  font-size: 12px;
  color: var(--red-600);
  width: 400px;
  margin-top: 0.25rem;
  &:before {
    content: "ðŸ˜‚  ";
    font-size: 15px;
  }
  @media (prefers-color-scheme: dark) {
    color: var(--red-300);
  }
`;


function SelectInput({label,...props}){
	const [field,meta]=useField(props);

	return (
			<div>
				<StyledLabel htmlFor={props.name}>{label}</StyledLabel>
				<StyledSelect {...field} {...props}/>
				{meta.touched && meta.error ? (<StyledErrorMessage>{meta.error}
				</StyledErrorMessage>):null}
			</div>

		);
}

class LoginForm extends React.Component{
	constructor(props){
		super(props);
		this.state={
			"isLogggedIn":false,

		};
		this.handleLogin=this.handleLogin.bind(this);
	}
	handleLogin(event){
		alert('Login Succesful : ' + this.state.isLogggedIn);

	}
	render(){

		return(
			<div>
			<h1>Login</h1>
			<Formik
			initialValues={{
	          username: "",
	          password: "",
	        }}
	        validationSchema={Yup.object({
	          username: Yup.string()
	          	.min(4)
	            .max(15, "Must be between 4 and 15 characters")
	            .required("Required"),
	          password: Yup.string()
	          	.min(8)
	            .max(20, "Must be between 8 and 20 characters")
	            .required("Required"),
	        	})}
	        onSubmit={async (values, { setSubmitting }) => {
	        	const requestOptions = {
			        method: 'POST',
			        headers: {'Accept': 'application/json',
			        		  'Content-Type': 'application/x-www-form-urlencoded'
			        		},
			        body: JSON.stringify({username: values.username,
			        					  password:values.password})
		    	};
				const response = await fetch('http://127.0.0.1:5000/login-check', requestOptions);
    			const data = await response.json();
    			console.log(data);
	          	await new Promise(r => setTimeout(r, 500));
	          	setSubmitting(false);
	          	if (data.isLoggedIn){
	          		this.setState({ isLogggedIn: true });
	          		this.handleLogin();
	          	}
        	}}
	        >
	        <Form>
				<TextInput 
					label="Username "
					name="username"
					type="text"
					placeholder="Your Username"/>

				<TextInput 
					label="Password "
					name="password"
					type="password"
					placeholder="Enter your password"/>

				<button type="submit" 
						// onClick={this.handleSubmit}
						>Login</button>
				<SelectInput label="Job Type" name="jobType">
		            <option value="">Select a job type</option>
		            <option value="designer">Designer</option>
		            <option value="development">Developer</option>
		            <option value="product">Product Manager</option>
		            <option value="other">Other</option>
          		</SelectInput>
			</Form>
			</Formik>
			</div>
			);
		}

}


