import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import { Formik, Form, useField, useFormikContext } from "formik";
import styled from "@emotion/styled";
import "./styles.css";
import "./styles-custom.css";
import * as Yup from "yup";


class EssayForm extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            value: 'Annen yanÄ±mda',
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

    }
    handleChange(event) {
        this.setState({ value: event.target.value });
    }
    handleSubmit(event) {
        alert('submission made : ' + this.state.value);
        event.preventDefault();
    }

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
        		<label>
        		Essay:

        		<textarea value={this.state.value} onClick={this.handleChange}/>
        		</label>
        		<input type="submit" value="Submit" />
        	</form>
        );
    }
}

class FlavourForm extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            value: 'niggas',

        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleChange(event) {
        this.setState({ value: event.target.value });
    }
    handleSubmit(event) {
        alert('Your favourite flavor is ' + this.state.value);
        event.preventDefault();
    }
    render() {
        return (
            <form onSubmit={this.handleSubmit}>
				<label>
				fLAOUER:
				<select value={this.state.value} onChange={this.handleChange}>
					<option value="annen">annane</option>
					<option value="baban">baban</option>
					<option value="nenen">niggas</option>
					<option value="dedn">dden</option>
				</select>
				</label>
				<input type="submit" value="Submit"/>

			</form>

        );
    }
}

class Rezervation extends React.Component {
	constructor(props){
		super(props);
		this.state={
			isGoing:true,
			numberOfGuests : 2,

		};
		this.handleInputChange=this.handleInputChange.bind(this);
		this.handleSubmit=this.handleSubmit.bind(this);

	}
	handleInputChange(event){
		const target = event.target;
		const value = target.name==="isGoing" ? target.checked : target.value;
		const name = target.name;

		this.setState({
			[name]:value
		});
	}

	handleSubmit(event){
		alert("GetireceÄŸin "+this.state.numberOfGuests+" kiÅŸiyi " + (this.state.isGoing ? "sikeyim":"sikmeyeyim") );
		event.preventDefault();
	}

	render(){
		return(
			<form onSubmit={this.handleSubmit}>
				<label> Going?:
				<input 
					name="isGoing"
					type="checkbox"
					checked={this.state.isGoing}
					onChange={this.handleInputChange} />

				</label>
				<br/>
				<label>
					Num of guests:
					<input
						name="numberOfGuests"
						type="number"
						value = {this.state.numberOfGuests}
						onChange={this.handleInputChange}/>
				</label>
				<br/>
				<input type="submit" value="Submit"/>
			</form>
			);
	}
}


function TextInput({label,...props}){
	const [field,meta] = useField(props);
	const isPassword=props.name==="password"?true:false;
	return(
			<div>
				<label htmlFor = {props.name}>{label}</label>
				<input className="text-input" {...field} {...props} />
				{meta.touched && meta.error ? (<StyledErrorMessage>{meta.error}

				</StyledErrorMessage>):null}

			</div>

		);
}

// Styled components ....

const StyledLabel = styled.label`
  margin-top: 1rem;
`;

const StyledSelect = styled.select`
  color: var(--blue);
`;

const StyledErrorMessage = styled.div`
  font-size: 12px;
  color: var(--red-600);
  width: 400px;
  margin-top: 0.25rem;
  &:before {
    content: "ðŸ˜‚  ";
    font-size: 15px;
  }
  @media (prefers-color-scheme: dark) {
    color: var(--red-300);
  }
`;


function SelectInput({label,...props}){
	const [field,meta]=useField(props);

	return (
			<div>
				<StyledLabel htmlFor={props.name}>{label}</StyledLabel>
				<StyledSelect {...field} {...props}/>
				{meta.touched && meta.error ? (<StyledErrorMessage>{meta.error}
				</StyledErrorMessage>):null}
			</div>

		);
}

class LoginForm extends React.Component{
	constructor(props){
		super(props);
		this.state={
			"isLogggedIn":false,

		};
		this.handleLogin=this.handleLogin.bind(this)
	}
	handleLogin(event){
		alert('submission made : ' + this.state.isLogggedIn);
		ReactDOM.render(<Rezervation />, document.getElementById("root"));

	}
	render(){

		return(
			<div>
			<h1> Login</h1>
			<Formik
			initialValues={{
	          username: "",
	          password: "",
	        }}
	        validationSchema={Yup.object({
	          username: Yup.string()
	          	.min(4)
	            .max(15, "Must be between 4 and 15 characters")
	            .required("Required"),
	          password: Yup.string()
	          	.min(8)
	            .max(20, "Must be between 8 and 20 characters")
	            .required("Required"),
	        	})}
	        onSubmit={async (values, { setSubmitting }) => {
	        	const requestOptions = {
			        method: 'POST',
			        headers: {'Accept': 'application/json',
			        		  'Content-Type': 'application/x-www-form-urlencoded'
			        		},
			        body: JSON.stringify({username: values.username,
			        					  password:values.password})
		    	};
				const response = await fetch('http://127.0.0.1:5000/login-check', requestOptions);
    			const data = await response.json();
    			console.log(data);
	          	await new Promise(r => setTimeout(r, 500));
	          	setSubmitting(false);
	          	if (data.isLoggedIn){
	          		this.setState({ isLogggedIn: true });
	          		this.handleLogin()
	          	}
        	}}
	        >
	        <Form>
				<TextInput 
					label="Username "
					name="username"
					type="text"
					placeholder="Your Username"/>

				<TextInput 
					label="Password "
					name="password"
					type="password"
					placeholder="Enter your password"/>

				<button type="submit" 
						// onClick={this.handleSubmit}
						>Login</button>
			</Form>
			</Formik>
			</div>
			);
		}

}


